import React from "react"

class TodoItem extends React.Component {
	render() {
		console.log(props)
		return(
			<div className="todo-item"> 
				<input 
					type="checkbox" 
					checked={props.todo.completed}
					onChange={() => props.handleChange(props.item.id)}
				/>
				<p>{props.todo.text}</p>
			</div>
			)
	}
}

export default TodoItem
